import React from 'react'
import StarRating from './StarRating'

const Reviews = ({reviews}) => {
    return (
        <div className="row row-cols-3 mb2">
            {reviews.map((review)=>{
                return (
                <div className="card text-white bg-primary mb-3 mr-4" style={{maxWidth:"30%"}}>
                <div className="card-header d-flex justify-content-between">
                    <span>{reviews.name}</span>
                    <span><StarRating rating={reviews.rating}/></span>
                </div>
                <div className="card-body">
                    <p className="card-text">reviews.review</p>
                </div>
                </div> 
            );
            })
            }

            {/* below classes are from bootstrap */}
