import React,{useEffect,useContext} from 'react'
import RestaurantFinder from "../API/RestaurantFinder"
import { RestaurantsContext } from '../context/RestaurantsContext'


const RestaurantList = () => {
    //desctructuring from context 
    const {restaurants,setRestaurants} = useContext(RestaurantsContext)

    const getTodos= async()=>{
        try {
            const responce = await fetch(url_host);
            const jsonData = await responce.json();

            setTodos(jsonData);

            console.log(jsonData);
        } catch (error) {

        }
    }

useEffect( ()=> {
    //watch video at 3:32:00, which explains why it needs fetchData
    const fetchData = async ()=> {
        try {
            const responce = await RestaurantFinder.get("/");
            setRestaurants(responce.data.data.restaurants);
            console.log(restaurants);
        } catch (error) {

        }
    }
    fetchData();
},[])

    return (
        <div className="list-group">
            <table className="table table-hover table-dark">
               <thead>
                   <tr className="bg-primary">
                        <th scope="col">Restaurant</th>
                        <th scope="col">Location</th>
                        <th scope="col">Price Range</th>
                        <th scope="col">Ratings</th>
                        <th scope="col">Edit</th>   
                        <th scope="col">Delete</th>
                   </tr>
               </thead> 
            <tbody>
                <tr>
                   <td>Mcdonalds</td>
                   <td>NY</td> 
                   <td>$$</td> 
                   <td>Ratings</td>  
                   <td><button className="btn btn-warning">Updates</button></td> 
                   <td><button className="btn btn-danger">Delete</button></td> 
                </tr>
            </tbody>
            </table>
        </div>
    )
}

export default RestaurantList
